mapper.update:
UPDATE order_master
order_status = #{orderStatus}, pay_status = #{payStatus},
WHERE order_id = #{orderId}

mapper.select:
SELECT * FROM order_master
WHERE user_id = #{userId}
      and order_status = #{orderStatus}
      and pay_status = #{payStatus}

SELECT * FROM order_master
WHERE seller_id = #{sellerId}
      and order_status = #{orderStatus}
      and pay_status = #{payStatus}
